    RETURN QUERY
        SELECT *
        FROM FUNC_CHECKLIST_OFFLINE_UPDATE_VERSAO_DADOS_UNIDADE((SELECT CM.COD_UNIDADE
                                                                 FROM CHECKLIST_MODELO CM
                                                                 WHERE CM.CODIGO = F_COD_MODELO_CHECKLIST));
