create or replace function public.func_checklist_offline_update_versao_dados_unidade_veiculo(f_cod_veiculo bigint)
    returns TABLE
            (
                empresa_checklist_offline_bloqueado boolean,
                versao_dados_checklist_unidade      bigint
            )
    language plpgsql
as
$$
BEGIN
